# This script iterates through a list of Windows features and disables them
# if they are currently installed and enabled.

# A list of features to check and remove.
$featuresToRemove = @(
    'MediaPlayback';
    'MicrosoftWindowsPowerShellV2Root';
    'Recall';
);

# The primary logic.
$featuresToRemove | ForEach-Object {
    # Disable the feature and suppress any errors if it's not installed or already disabled.
    Disable-WindowsOptionalFeature -Online -Remove -NoRestart -ErrorAction SilentlyContinue -FeatureName $_;
}


